mapper.update:
update course
name = #{name}, coach_id = #{coach_id}, time_slot_id = #{time_slot_id}, capacity = #{capacity}, number_of_member = #{number_of_member}, room_number = #{room_number}, start_date = #{start_date}, end_date = #{end_date}, state = #{state}, description = #{description},
where id = #{id};

update course
set number_of_member = number_of_member + 1
where id = #{id};

update course
set number_of_member = number_of_member - 1
where id = #{id};
