mapper.insert:
insert into member
(password, name, gender, date_of_birth, phone_number, validity, number_of_course)
VALUES
(#{password},#{name},#{gender},#{date_of_birth},#{phone_number},#{validity},#{number_of_course});


    SELECT last_insert_id();

mapper.update:
update member
password = #{password}, name = #{name}, gender = #{gender}, date_of_birth = #{date_of_birth}, phone_number = #{phone_number}, validity = #{validity}, state = #{state}, number_of_course = #{number_of_course},
where id = #{id};
